import 'package:flare_flutter/flare_actor.dart';import 'package:flutter/cupertino.dart';class bottomBar extends StatefulWidget {  @override  createState() => bottomBarState();}class bottomBarState extends State<bottomBar> {  @override  Widget build(BuildContext context) {    return Row(      children: <Widget>[        _flare(false),        _flare(true),        _flare(false),      ],    );  }}Widget _flare(bool selected) {  return Expanded(    child: Container(      color: Color(0xdf0080ff),      child: FlareActor(        'asset/animation/navBar.flr',        alignment: Alignment.bottomCenter,        fit: BoxFit.contain,        animation: selected ? 'Selected' : 'NotSelected',        color: Color(0xffffffff),      ),    ),  );}